mapper.select:
select A.a_wage, A.a_time,
case when hour(timediff(now(), A.ua_date)) < 1 then concat(timestampdiff(minute, A.ua_date, now()),'분') when hour(timediff(now(), A.ua_date)) < 24 then concat(timestampdiff(hour, A.ua_date, now()),'시간') when hour(timediff(now(), A.ua_date)) < 168 then concat(timestampdiff(day, A.ua_date, now()),'일') when hour(timediff(now(), A.ua_date)) < 720 then concat(timestampdiff(week, A.ua_date, now()),'주') when hour(timediff(now(), A.ua_date)) < 8760 then concat(timestampdiff(month, A.ua_date, now()),'개월') else concat(timestampdiff(year, A.ua_date, now()),'년') end as uptime,
B.s_name, B.s_address, B.s_picture, B.s_tag1, B.s_tag2, B.s_tag3, B.s_tag4, B.s_tag5, C.r_name from job_ads as A inner join shop_info as B on A.s_number = B.s_number inner join employer as C on A.r_id = C.r_id order by A.a_number desc limit 0, 6;
